import React, { useState, useEffect } from 'react';
import axios from 'axios';
import ProjectForm from './components/ProjectForm';
import ProjectList from './components/ProjectList';

function App() {
  const [projects, setProjects] = useState([]); // State to store projects
  const [error, setError] = useState(null); // State to handle errors

  const fetchProjects = async () => {
    try {
      const { data } = await axios.get('http://localhost:4000/api/projects');
      setProjects(data);
    } catch (err) {
      console.error('Error fetching projects:', err.message);
      setError('Failed to fetch projects. Please try again later.');
    }
  };

  useEffect(() => {
    fetchProjects();
  }, []);

  const handleCreate = async (project) => {
    try {
      await axios.post('http://localhost:4000/api/projects', project);
      fetchProjects();
    } catch (err) {
      console.error('Error creating project:', err.message);
      alert('Failed to create project. Check your backend server.');
    }
  };

  const handleUpdate = async (id, updatedProject) => {
    try {
      // Send the PUT request with updated data
      await axios.put(`http://localhost:4000/api/projects/${id}`, updatedProject);
      fetchProjects(); // Refresh the project list after updating
      alert('Project updated successfully!');
    } catch (err) {
      console.error('Error updating project:', err.message);
      alert('Failed to update the project. Please try again.');
    }
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:4000/api/projects/${id}`);
      fetchProjects();
    } catch (err) {
      console.error('Error deleting project:', err.message);
      alert('Failed to delete project. Please try again.');
    }
  };

  return (
    <div className="App" style={{ padding: '20px', fontFamily: 'Arial, sans-serif' }}>
      <header style={{ textAlign: 'center', marginBottom: '20px' }}>
        <h1 style={{ color: '#4caf50' }}>Abdiwahab Maal Project Management</h1>
      </header>

      {/* Main container for the two sections */}
      <div
        style={{
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'flex-start',
          gap: '20px',
        }}
      >
        {/* Project List on the left */}
        <div style={{ flex: '3' }}>
          <ProjectList
            projects={projects}
            onUpdate={handleUpdate}
            onDelete={handleDelete}
          />
        </div>

        {/* Add Project Form on the right */}
        <div
          style={{
            flex: '1',
            backgroundColor: '#1e1e2f',
            padding: '20px',
            borderRadius: '10px',
            boxShadow: '0 4px 10px rgba(0, 0, 0, 0.3)',
            color: '#fff',
          }}
        >
          <h2 style={{ color: '#4caf50', textAlign: 'center' }}>Add A New Project</h2>
          <ProjectForm onCreate={handleCreate} />
        </div>
      </div>
    </div>
  );
}

export default App;
