import React, { useState, useEffect } from 'react';
import axios from 'axios';
import ProjectForm from './components/ProjectForm';
import ProjectList from './components/ProjectList';

function App() {
  const [projects, setProjects] = useState([]); // State to store projects
  const [error, setError] = useState(null); // State to handle errors

  
  const fetchProjects = async () => {
    try {
      const { data } = await axios.get('http://127.0.0.1:8000/api/projects'); // Use your API URL
      setProjects(data); // Update the state with fetched data
    } catch (error) {
      console.error('Error fetching projects:', error.message); // Log errors
    }
  };
  
  
  const handleCreate = async (project) => {
    try {
      await axios.post('http://127.0.0.1:8000/api/projects', project); // Laravel endpoint
      fetchProjects(); // Refresh project list
    } catch (error) {
      console.error('Error creating project:', error.message);
    }
  };
  
  const handleUpdate = async (id, project) => {
    await axios.put(`http://127.0.0.1:8000/api/projects/${id}`, project);
    fetchProjects();
  };
  
  const handleDelete = async (id) => {
    await axios.delete(`http://127.0.0.1:8000/api/projects/${id}`);
    fetchProjects();
  };
  return (
    <div className="App" style={{ padding: '20px', fontFamily: 'Arial, sans-serif' }}>
      <header style={{ textAlign: 'center', marginBottom: '20px' }}>
        <h1 style={{ color: '#4caf50' }}>Abdiwahab Maal Project Management</h1>
      </header>

      {/* Main container for the two sections */}
      <div
        style={{
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'flex-start',
          gap: '20px',
        }}
      >
        {/* Project List on the left */}
        <div style={{ flex: '3' }}>
          <ProjectList
            projects={projects}
            onUpdate={handleUpdate}
            onDelete={handleDelete}
          />
        </div>

        {/* Add Project Form on the right */}
        <div
          style={{
            flex: '1',
            backgroundColor: '#1e1e2f',
            padding: '20px',
            borderRadius: '10px',
            boxShadow: '0 4px 10px rgba(0, 0, 0, 0.3)',
            color: '#fff',
          }}
        >
          <h2 style={{ color: '#4caf50', textAlign: 'center' }}>Add A New Project</h2>
          <ProjectForm onCreate={handleCreate} />
        </div>
      </div>
    </div>
  );
}

export default App;
