import React, { useState, useEffect } from 'react';
import axios from 'axios';
import ProjectForm from './components/ProjectForm';
import ProjectList from './components/ProjectList';

function App() {
  const [projects, setProjects] = useState([]); // State to store project data

  // Fetch projects from the backend
  const fetchProjects = async () => {
    try {
      const { data } = await axios.get('http://127.0.0.1:8000/api/projects'); // Backend API URL
      setProjects(data); // Update state with fetched data
    } catch (error) {
      console.error('Error fetching projects:', error.message); // Log errors
    }
  };

  // Call fetchProjects on component mount
  useEffect(() => {
    fetchProjects(); // Fetch data when the component is mounted
  }, []); // Empty dependency array ensures it runs once

  const handleCreate = async (project) => {
    try {
      await axios.post('http://127.0.0.1:8000/api/projects', project);
      fetchProjects(); // Refresh the project list
    } catch (error) {
      console.error('Error creating project:', error.message);
    }
  };

  const handleUpdate = async (id, updatedProject) => {
    try {
      await axios.put(`http://127.0.0.1:8000/api/projects/${id}`, updatedProject);
      fetchProjects(); // Refresh the project list
    } catch (error) {
      console.error('Error updating project:', error.message);
    }
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://127.0.0.1:8000/api/projects/${id}`);
      fetchProjects(); // Refresh the project list
    } catch (error) {
      console.error('Error deleting project:', error.message);
    }
  };

  return (
    <div className="App">
      <h1 style={{ textAlign: 'center', color: '#4caf50' }}>Project Management</h1>
      <div style={{ display: 'flex', justifyContent: 'space-between', padding: '20px' }}>
        <ProjectForm onCreate={handleCreate} />
        <ProjectList projects={projects} onUpdate={handleUpdate} onDelete={handleDelete} />
      </div>
    </div>
  );
}

export default App;
