import React, { useState } from 'react';

function ProjectForm({ onCreate }) {
  const [name, setName] = useState('');
  const [description, setDescription] = useState('');
  const [status, setStatus] = useState('active');

  const handleSubmit = (e) => {
    e.preventDefault();
    onCreate({ name, description, status });
    setName('');
    setDescription('');
    setStatus('active');
  };

  return (
    <form
      onSubmit={handleSubmit}
      style={{
        backgroundColor: '#1e1e2f',
        padding: '20px',
        borderRadius: '10px',
        boxShadow: '0 4px 10px rgba(0, 0, 0, 0.3)',
        color: '#fff',
        maxWidth: '400px',
        margin: 'auto',
      }}
    >
      <h2 style={{ textAlign: 'center', marginBottom: '20px', color: '#4caf50' }}>
        Create Project
      </h2>
      <div style={{ marginBottom: '15px' }}>
        <label style={{ display: 'block', marginBottom: '5px' }}>Project Name</label>
        <input
          type="text"
          placeholder="Project Name"
          value={name}
          onChange={(e) => setName(e.target.value)}
          style={{
            width: '100%',
            padding: '10px',
            borderRadius: '5px',
            border: '1px solid #ccc',
            backgroundColor: '#2e2e3d',
            color: '#fff',
          }}
        />
      </div>
      <div style={{ marginBottom: '15px' }}>
        <label style={{ display: 'block', marginBottom: '5px' }}>Description</label>
        <textarea
          placeholder="Description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          style={{
            width: '100%',
            padding: '10px',
            borderRadius: '5px',
            border: '1px solid #ccc',
            backgroundColor: '#2e2e3d',
            color: '#fff',
          }}
        ></textarea>
      </div>
      <div style={{ marginBottom: '20px' }}>
        <label style={{ display: 'block', marginBottom: '5px' }}>Status</label>
        <select
          value={status}
          onChange={(e) => setStatus(e.target.value)}
          style={{
            width: '100%',
            padding: '10px',
            borderRadius: '5px',
            border: '1px solid #ccc',
            backgroundColor: '#2e2e3d',
            color: '#fff',
          }}
        >
          <option value="active">Active</option>
          <option value="inactive">Inactive</option>
          <option value="completed">Completed</option>
        </select>
      </div>
      <button
        type="submit"
        style={{
          width: '100%',
          padding: '10px',
          borderRadius: '5px',
          backgroundColor: '#4caf50',
          border: 'none',
          color: '#fff',
          cursor: 'pointer',
          fontSize: '16px',
        }}
      >
        Create Project
      </button>
    </form>
  );
}

export default ProjectForm;
