import React, { useState, useEffect } from 'react';
import axios from 'axios';
import ProjectForm from './components/ProjectForm';
import ProjectList from './components/ProjectList';

function App() {
  const [projects, setProjects] = useState([]);

  const fetchProjects = async () => {
    const { data } = await axios.get('http://localhost:4000/api/projects');
    setProjects(data);
  };

  useEffect(() => {
    fetchProjects();
  }, []);

  const handleCreate = async (project) => {
    try {
      // Send POST request to backend API
      await axios.post('http://localhost:4000/api/projects', project);
      fetchProjects(); // Refresh the project list after adding
    } catch (error) {
      console.error('Error creating project:', error.message); // Log error for debugging
      alert('Failed to create project. Check your backend server.'); // Show user-friendly error message
    }
  };
  const handleUpdate = async (id, project) => {
    await axios.put(`http://localhost:4000/api/projects/${id}`, project);
    fetchProjects();
  };

  const handleDelete = async (id) => {
    await axios.delete(`http://localhost:4000/api/projects/${id}`);
    fetchProjects();
  };

  return (
    <div className="App">
      <h1>Project Manager</h1>
      <ProjectForm onCreate={handleCreate} />
      <ProjectList
        projects={projects}
        onUpdate={handleUpdate}
        onDelete={handleDelete}
      />
    </div>
  );
}

export default App;
