import React, { useState, useEffect } from 'react';
import axios from 'axios';
import ProjectForm from './components/ProjectForm';
import ProjectList from './components/ProjectList';

function App() {
  const [projects, setProjects] = useState([]);

  const fetchProjects = async () => {
    try {
      const { data } = await axios.get('http://127.0.0.1:8000/api/projects');
      setProjects(data);
    } catch (error) {
      console.error('Error fetching projects:', error.message);
    }
  };

  useEffect(() => {
    fetchProjects();
  }, []);

  const handleCreate = async (project) => {
    try {
      await axios.post('http://127.0.0.1:8000/api/projects', project);
      fetchProjects();
    } catch (error) {
      console.error('Error creating project:', error.message);
    }
  };

  const handleUpdate = async (id, updatedProject) => {
    try {
      await axios.put(`http://127.0.0.1:8000/api/projects/${id}`, updatedProject);
      fetchProjects();
    } catch (error) {
      console.error('Error updating project:', error.message);
    }
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://127.0.0.1:8000/api/projects/${id}`);
      fetchProjects();
    } catch (error) {
      console.error('Error deleting project:', error.message);
    }
  };

  return (
    <div>
      {/* Embedded CSS */}
      <style>{`
        body {
          font-family: Arial, sans-serif;
          margin: 0;
          padding: 0;
          background-color: #121212;
          color: #fff;
        }

        .main-container {
          display: flex;
          justify-content: space-between;
          padding: 40px;
          gap: 30px;
        }

        .create-form {
          flex: 1;
          max-width: 350px;
          background-color: #1e1e2f;
          color: #fff;
          padding: 20px;
          border-radius: 10px;
          box-shadow: 0 4px 8px rgba(0, 0, 0, 0.3);
        }

        .create-form h3 {
          color: #fdd835; /* Yellow color for title */
          text-align: center;
          margin-bottom: 20px;
        }

        .create-form input,
        .create-form textarea,
        .create-form select {
          width: 100%;
          margin-bottom: 15px;
          padding: 10px;
          border-radius: 5px;
          border: 1px solid #ccc;
          background-color: #2e2e3d;
          color: #fff;
        }

        .create-form button {
          width: 100%;
          background-color: #fdd835; /* Yellow background */
          border: none;
          padding: 10px;
          color: #121212; /* Dark text on yellow */
          font-weight: bold;
          border-radius: 5px;
          cursor: pointer;
        }

        .create-form button:hover {
          background-color: #ffeb3b; /* Lighter yellow */
        }

        .project-list {
          flex: 2;
          display: flex;
          flex-wrap: wrap;
          gap: 20px;
          justify-content: flex-start;
        }

        .project-card {
          background-color: #fdd835; /* Yellow background for cards */
          color: #121212; /* Dark text on yellow */
          border-radius: 10px;
          box-shadow: 0 4px 8px rgba(0, 0, 0, 0.3);
          padding: 20px;
          width: 300px;
          display: flex;
          flex-direction: column;
          justify-content: space-between;
        }

        .project-card h3 {
          color: #121212;
          margin-bottom: 10px;
        }

        .project-card p {
          margin: 5px 0;
          color: #212121;
        }

        .project-card .actions {
          margin-top: 15px;
          display: flex;
          justify-content: space-between;
        }

        .project-card button {
          border: none;
          padding: 10px 15px;
          color: #121212; /* Dark text on yellow */
          font-weight: bold;
          border-radius: 5px;
          cursor: pointer;
        }

        .project-card button.update {
          background-color: #fdd835; /* Yellow */
        }

        .project-card button.update:hover {
          background-color: #ffeb3b; /* Lighter yellow */
        }

        .project-card button.delete {
          background-color: #d32f2f;
        }

        .project-card button.delete:hover {
          background-color: #f44336;
        }

        @media (max-width: 768px) {
          .main-container {
            flex-direction: column;
            align-items: center;
          }

          .project-list {
            justify-content: center;
          }
        }
      `}</style>

      {/* Page Header */}
      <h1 style={{ textAlign: 'center', color: '#fdd835', margin: '20px 0' }}>
        Project Management
      </h1>

      <div className="main-container">
        {/* Create Project Form */}
        <div className="create-form">
          <h3>Create Project</h3>
          <ProjectForm onCreate={handleCreate} />
        </div>

        {/* Project List */}
        <div className="project-list">
          <ProjectList projects={projects} onUpdate={handleUpdate} onDelete={handleDelete} />
        </div>
      </div>
    </div>
  );
}

export default App;
