import React, { useState } from 'react';

function ProjectForm({ onCreate }) {
  const [name, setName] = useState('');
  const [description, setDescription] = useState('');
  const [status, setStatus] = useState('active');
  const [errors, setErrors] = useState({}); // Track validation errors

  const validateForm = () => {
    const newErrors = {};

    if (!name.trim()) {
      newErrors.name = 'Project name is required.';
    }
    if (!['active', 'inactive', 'completed'].includes(status)) {
      newErrors.status = 'Invalid status selected.';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0; // Return true if no errors
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!validateForm()) return; // Stop if validation fails

    onCreate({ name, description, status }); // Pass data to the parent component
    setName('');
    setDescription('');
    setStatus('active');
    setErrors({});
  };

  return (
    <form onSubmit={handleSubmit} style={{ color: '#fff' }}>
      <input
        type="text"
        placeholder="Project Name"
        value={name}
        onChange={(e) => setName(e.target.value)}
        style={{
          width: '100%',
          marginBottom: '10px',
          padding: '10px',
          borderRadius: '5px',
          border: '1px solid #ccc',
          backgroundColor: '#2e2e3d',
          color: '#fff',
        }}
      />
      {errors.name && <p style={{ color: 'red', marginBottom: '10px' }}>{errors.name}</p>}

      <textarea
        placeholder="Description"
        value={description}
        onChange={(e) => setDescription(e.target.value)}
        style={{
          width: '100%',
          marginBottom: '10px',
          padding: '10px',
          borderRadius: '5px',
          border: '1px solid #ccc',
          backgroundColor: '#2e2e3d',
          color: '#fff',
        }}
      ></textarea>

      <select
        value={status}
        onChange={(e) => setStatus(e.target.value)}
        style={{
          width: '100%',
          marginBottom: '10px',
          padding: '10px',
          borderRadius: '5px',
          border: '1px solid #ccc',
          backgroundColor: '#2e2e3d',
          color: '#fff',
        }}
      >
        <option value="active">Active</option>
        <option value="inactive">Inactive</option>
        <option value="completed">Completed</option>
      </select>
      {errors.status && <p style={{ color: 'red', marginBottom: '10px' }}>{errors.status}</p>}

      <button
        type="submit"
        style={{
          width: '100%',
          backgroundColor: '#fdd835',
          border: 'none',
          padding: '10px',
          color: '#121212',
          fontWeight: 'bold',
          borderRadius: '5px',
          cursor: 'pointer',
        }}
      >
        Create Project
      </button>
    </form>
  );
}

export default ProjectForm;
