import React, { useState } from 'react';

function ProjectForm({ onCreate }) {
  const [name, setName] = useState('');
  const [description, setDescription] = useState('');
  const [status, setStatus] = useState('active');
  const [errors, setErrors] = useState({}); // Track validation errors

  const validateForm = () => {
    const newErrors = {};
    if (!name.trim()) {
      newErrors.name = 'Project name is required.';
    }
    if (!['active', 'inactive', 'completed'].includes(status)) {
      newErrors.status = 'Invalid status selected.';
    }
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0; // Return true if no errors
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!validateForm()) return; // Stop if validation fails

    onCreate({ name, description, status }); // Pass data to the parent component
    setName('');
    setDescription('');
    setStatus('active');
    setErrors({});
  };

  return (
    <form onSubmit={handleSubmit} style={formStyles.container}>
      <h3 style={formStyles.heading}>Create Project</h3>

      {/* Name Input */}
      <input
        type="text"
        placeholder="Project Name"
        value={name}
        onChange={(e) => setName(e.target.value)}
        style={formStyles.input}
      />
      {errors.name && <p style={formStyles.error}>{errors.name}</p>}

      {/* Description Input */}
      <textarea
        placeholder="Description"
        value={description}
        onChange={(e) => setDescription(e.target.value)}
        style={formStyles.textarea}
      ></textarea>

      {/* Status Dropdown */}
      <select
        value={status}
        onChange={(e) => setStatus(e.target.value)}
        style={formStyles.select}
      >
        <option value="active">Active</option>
        <option value="inactive">Inactive</option>
        <option value="completed">Completed</option>
      </select>
      {errors.status && <p style={formStyles.error}>{errors.status}</p>}

      {/* Submit Button */}
      <button type="submit" style={formStyles.button}>
        Create Project
      </button>
    </form>
  );
}

const formStyles = {
  container: {
    backgroundColor: '#1e1e2f',
    color: '#fff',
    padding: '20px',
    borderRadius: '10px',
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.3)',
    maxWidth: '350px',
    margin: '0 auto',
  },
  heading: {
    color: '#fdd835',
    textAlign: 'center',
    marginBottom: '20px',
  },
  input: {
    width: '100%',
    marginBottom: '10px',
    padding: '10px',
    borderRadius: '5px',
    border: '1px solid #ccc',
    backgroundColor: '#2e2e3d',
    color: '#fff',
  },
  textarea: {
    width: '100%',
    marginBottom: '10px',
    padding: '10px',
    borderRadius: '5px',
    border: '1px solid #ccc',
    backgroundColor: '#2e2e3d',
    color: '#fff',
  },
  select: {
    width: '100%',
    marginBottom: '10px',
    padding: '10px',
    borderRadius: '5px',
    border: '1px solid #ccc',
    backgroundColor: '#2e2e3d',
    color: '#fff',
  },
  button: {
    width: '100%',
    backgroundColor: '#fdd835',
    border: 'none',
    padding: '10px',
    color: '#121212',
    fontWeight: 'bold',
    borderRadius: '5px',
    cursor: 'pointer',
  },
  error: {
    color: 'red',
    marginBottom: '10px',
    fontSize: '0.9em',
  },
};

export default ProjectForm;
